package singledimensionarray;

public class SingleDimensionArrray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int arr[]={30,10,50,80};
		for(int i=0;i<4;i++)
		{
			System.out.println(arr[i]);
		}

	}

}
