package singledimensionarray;

public class SingleDimensionArrrayEx4 {
	int arr1[] = {30,10,50,60,20};
	char arr2[] = {'a','r','u','l'};
	String arr3[] = {"Black","White","Blue"};
	{
	  System.out.println("java util.Array to String(arr1)");//Works with any type of array
	  System.out.println("java.util.Array to String(arr2)");
	  System.out.println("java util.Array to String(arr3)");
	  System.out.println("String value of(arr3)");//Work with only char type
	}
}
	
